  #[allow(clippy::clone_on_copy)]
  #[allow(clippy::vec_init_then_push)]
  pub fn invoke_signed(&self, signers_seeds: &[&[&[u8]]]) -> solana_program::entrypoint::ProgramResult {
          let args = InitializeMarketInstructionArgs {
                                                              market_size_params: self.instruction.market_size_params.clone().expect("market_size_params is not set"),
                                                                  num_quote_lots_per_quote_unit: self.instruction.num_quote_lots_per_quote_unit.clone().expect("num_quote_lots_per_quote_unit is not set"),
                                                                  tick_size_in_quote_lots_per_base_unit: self.instruction.tick_size_in_quote_lots_per_base_unit.clone().expect("tick_size_in_quote_lots_per_base_unit is not set"),
                                                                  num_base_lots_per_base_unit: self.instruction.num_base_lots_per_base_unit.clone().expect("num_base_lots_per_base_unit is not set"),
                                                                  taker_fee_bps: self.instruction.taker_fee_bps.clone().expect("taker_fee_bps is not set"),
                                                                  fee_collector: self.instruction.fee_collector.clone().expect("fee_collector is not set"),
                                                                  raw_base_units_per_base_unit: self.instruction.raw_base_units_per_base_unit.clone(),
                                    };
        let instruction = InitializeMarketCpi {
        __program: self.instruction.__program,
                  
          phoenix_program: self.instruction.phoenix_program.expect("phoenix_program is not set"),
                  
          log_authority: self.instruction.log_authority.expect("log_authority is not set"),
                  
          market: self.instruction.market.expect("market is not set"),
                  
          market_creator: self.instruction.market_creator.expect("market_creator is not set"),
                  
          base_mint: self.instruction.base_mint.expect("base_mint is not set"),
                  
          quote_mint: self.instruction.quote_mint.expect("quote_mint is not set"),
                  
          base_vault: self.instruction.base_vault.expect("base_vault is not set"),
                  
          quote_vault: self.instruction.quote_vault.expect("quote_vault is not set"),
                  
          system_program: self.instruction.system_program.expect("system_program is not set"),
                  
          token_program: self.instruction.token_program.expect("token_program is not set"),
                          __args: args,
            };
    instruction.invoke_signed_with_remaining_accounts(signers_seeds, &self.instruction.__remaining_accounts)
  }
