impl<'a, 'b> TakeLoanV3CompressedCpiBuilder<'a, 'b> {
    pub fn new(program: &'b solana_program::account_info::AccountInfo<'a>) -> Self {
        let instruction = Box::new(TakeLoanV3CompressedCpiBuilderInstruction {
            __program: program,
            lender: None,
            borrower: None,
            loan: None,
            escrow: None,
            order_book: None,
            collateral_mint: None,
            tree_authority: None,
            log_wrapper: None,
            merkle_tree: None,
            system_program: None,
            token_program: None,
            mpl_bubblegum_program: None,
            compression_program: None,
            rent: None,
            expected_loan: None,
            nft_list_index: None,
            cnft_root: None,
            cnft_data_hash: None,
            cnft_creator_hash: None,
            cnft_nonce: None,
            cnft_index: None,
            __remaining_accounts: Vec::new(),
        });
        Self { instruction }
    }

    #[inline(always)]
    pub fn lender(
        &mut self,
        lender: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.lender = Some(lender);
        self
    }

    /// The borrower that will receive the tokens in exchange for collateral
    #[inline(always)]
    pub fn borrower(
        &mut self,
        borrower: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.borrower = Some(borrower);
        self
    }

    /// Expected loan prevents a replacement attack where the loan is replaced between the
    /// user signing take_loan and it hitting the chain
    #[inline(always)]
    pub fn loan(&mut self, loan: &'b solana_program::account_info::AccountInfo<'a>) -> &mut Self {
        self.instruction.loan = Some(loan);
        self
    }

    #[inline(always)]
    pub fn escrow(
        &mut self,
        escrow: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.escrow = Some(escrow);
        self
    }

    #[inline(always)]
    pub fn order_book(
        &mut self,
        order_book: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.order_book = Some(order_book);
        self
    }

    #[inline(always)]
    pub fn collateral_mint(
        &mut self,
        collateral_mint: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.collateral_mint = Some(collateral_mint);
        self
    }

    #[inline(always)]
    pub fn tree_authority(
        &mut self,
        tree_authority: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.tree_authority = Some(tree_authority);
        self
    }

    #[inline(always)]
    pub fn log_wrapper(
        &mut self,
        log_wrapper: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.log_wrapper = Some(log_wrapper);
        self
    }

    #[inline(always)]
    pub fn merkle_tree(
        &mut self,
        merkle_tree: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.merkle_tree = Some(merkle_tree);
        self
    }

    #[inline(always)]
    pub fn system_program(
        &mut self,
        system_program: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.system_program = Some(system_program);
        self
    }

    #[inline(always)]
    pub fn token_program(
        &mut self,
        token_program: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.token_program = Some(token_program);
        self
    }

    #[inline(always)]
    pub fn mpl_bubblegum_program(
        &mut self,
        mpl_bubblegum_program: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.mpl_bubblegum_program = Some(mpl_bubblegum_program);
        self
    }

    #[inline(always)]
    pub fn compression_program(
        &mut self,
        compression_program: &'b solana_program::account_info::AccountInfo<'a>,
    ) -> &mut Self {
        self.instruction.compression_program = Some(compression_program);
        self
    }

    #[inline(always)]
    pub fn rent(&mut self, rent: &'b solana_program::account_info::AccountInfo<'a>) -> &mut Self {
        self.instruction.rent = Some(rent);
        self
    }

    #[inline(always)]
    pub fn expected_loan(&mut self, expected_loan: String) -> &mut Self {
        self.instruction.expected_loan = Some(expected_loan);
        self
    }

    /// `[optional argument]`
    #[inline(always)]
    pub fn nft_list_index(&mut self, nft_list_index: u32) -> &mut Self {
        self.instruction.nft_list_index = Some(nft_list_index);
        self
    }

    #[inline(always)]
    pub fn cnft_root(&mut self, cnft_root: [u8; 32]) -> &mut Self {
        self.instruction.cnft_root = Some(cnft_root);
        self
    }

    #[inline(always)]
    pub fn cnft_data_hash(&mut self, cnft_data_hash: [u8; 32]) -> &mut Self {
        self.instruction.cnft_data_hash = Some(cnft_data_hash);
        self
    }

    #[inline(always)]
    pub fn cnft_creator_hash(&mut self, cnft_creator_hash: [u8; 32]) -> &mut Self {
        self.instruction.cnft_creator_hash = Some(cnft_creator_hash);
        self
    }

    #[inline(always)]
    pub fn cnft_nonce(&mut self, cnft_nonce: u64) -> &mut Self {
        self.instruction.cnft_nonce = Some(cnft_nonce);
        self
    }

    #[inline(always)]
    pub fn cnft_index(&mut self, cnft_index: u32) -> &mut Self {
        self.instruction.cnft_index = Some(cnft_index);
        self
    }

    /// Add an additional account to the instruction.
    #[inline(always)]
    pub fn add_remaining_account(
        &mut self,
        account: &'b solana_program::account_info::AccountInfo<'a>,
        is_writable: bool,
        is_signer: bool,
    ) -> &mut Self {
        self.instruction
            .__remaining_accounts
            .push((account, is_writable, is_signer));
        self
    }

    /// Add additional accounts to the instruction.
    ///
    /// Each account is represented by a tuple of the `AccountInfo`, a `bool` indicating whether the account is writable or not,
    /// and a `bool` indicating whether the account is a signer or not.
    #[inline(always)]
    pub fn add_remaining_accounts(
        &mut self,
        accounts: &[(
            &'b solana_program::account_info::AccountInfo<'a>,
            bool,
            bool,
        )],
    ) -> &mut Self {
        self.instruction
            .__remaining_accounts
            .extend_from_slice(accounts);
        self
    }

    #[inline(always)]
    pub fn invoke(&self) -> solana_program::entrypoint::ProgramResult { self.invoke_signed(&[]) }

    #[allow(clippy::clone_on_copy)]
    #[allow(clippy::vec_init_then_push)]
    pub fn invoke_signed(
        &self,
        signers_seeds: &[&[&[u8]]],
    ) -> solana_program::entrypoint::ProgramResult {
        let args = TakeLoanV3CompressedInstructionArgs {
            expected_loan: self
                .instruction
                .expected_loan
                .clone()
                .expect("expected_loan is not set"),
            nft_list_index: self.instruction.nft_list_index.clone(),
            cnft_root: self
                .instruction
                .cnft_root
                .clone()
                .expect("cnft_root is not set"),
            cnft_data_hash: self
                .instruction
                .cnft_data_hash
                .clone()
                .expect("cnft_data_hash is not set"),
            cnft_creator_hash: self
                .instruction
                .cnft_creator_hash
                .clone()
                .expect("cnft_creator_hash is not set"),
            cnft_nonce: self
                .instruction
                .cnft_nonce
                .clone()
                .expect("cnft_nonce is not set"),
            cnft_index: self
                .instruction
                .cnft_index
                .clone()
                .expect("cnft_index is not set"),
        };
        let instruction = TakeLoanV3CompressedCpi {
            __program: self.instruction.__program,

            lender: self.instruction.lender.expect("lender is not set"),

            borrower: self.instruction.borrower.expect("borrower is not set"),

            loan: self.instruction.loan.expect("loan is not set"),

            escrow: self.instruction.escrow.expect("escrow is not set"),

            order_book: self.instruction.order_book.expect("order_book is not set"),

            collateral_mint: self
                .instruction
                .collateral_mint
                .expect("collateral_mint is not set"),

            tree_authority: self
                .instruction
                .tree_authority
                .expect("tree_authority is not set"),

            log_wrapper: self
                .instruction
                .log_wrapper
                .expect("log_wrapper is not set"),

            merkle_tree: self
                .instruction
                .merkle_tree
                .expect("merkle_tree is not set"),

            system_program: self
                .instruction
                .system_program
                .expect("system_program is not set"),

            token_program: self
                .instruction
                .token_program
                .expect("token_program is not set"),

            mpl_bubblegum_program: self
                .instruction
                .mpl_bubblegum_program
                .expect("mpl_bubblegum_program is not set"),

            compression_program: self
                .instruction
                .compression_program
                .expect("compression_program is not set"),

            rent: self.instruction.rent.expect("rent is not set"),
            __args: args,
        };
        instruction.invoke_signed_with_remaining_accounts(
            signers_seeds,
            &self.instruction.__remaining_accounts,
        )
    }
}

#[derive(Clone, Debug)]
struct TakeLoanV3CompressedCpiBuilderInstruction<'a, 'b> {
    __program: &'b solana_program::account_info::AccountInfo<'a>,
    lender: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    borrower: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    loan: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    escrow: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    order_book: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    collateral_mint: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    tree_authority: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    log_wrapper: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    merkle_tree: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    system_program: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    token_program: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    mpl_bubblegum_program: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    compression_program: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    rent: Option<&'b solana_program::account_info::AccountInfo<'a>>,
    expected_loan: Option<String>,
    nft_list_index: Option<u32>,
    cnft_root: Option<[u8; 32]>,
    cnft_data_hash: Option<[u8; 32]>,
    cnft_creator_hash: Option<[u8; 32]>,
    cnft_nonce: Option<u64>,
    cnft_index: Option<u32>,
    /// Additional instruction accounts `(AccountInfo, is_writable, is_signer)`.
    __remaining_accounts: Vec<(
        &'b solana_program::account_info::AccountInfo<'a>,
        bool,
        bool,
    )>,
}
